	/**
	 * 获取URL参数
	 * //example getUrlParam('id') or getUrlParam('id','#')
	 */
	exports.getUrlParam = function(){
		var url = top.window.location.href;
		var u, params, StrBack = '', gg;
		if (arguments[arguments.length - 1] == "#") 
			u = url.split("#");
		else 
			u = url.split("?");
		if (u.length == 1) 
			params = '';
		else 
			params = u[1];
		
		if (params != '') {
			gg = params.split("&");
			var MaxI = gg.length,
			str = arguments[0] + "=";
			for (i = 0; i < MaxI; i++) {
				if (gg[i].indexOf(str) == 0) {
					StrBack = gg[i].replace(str, "");
					break;
				}
			}
		}
		return StrBack;
	};
	
