        var new_style_obj = {};
        for (var st in style_obj) {
            //如果是数字的话
            if ($.isNumeric(style_obj[st]) && style_obj[st] !== "") {
                new_style_obj[st] = style_obj[st] + units;
            } else {
                new_style_obj[st] = style_obj[st];
            }
        }
