	function $time33(str){
		//哈希time33算法
		for(var i = 0, len = str.length,hash = 5381; i < len; ++i){
		   hash += (hash << 5) + str.charAt(i).charCodeAt();
		};
		return hash & 0x7fffffff;
	}
	
